private fun String.toDirection(): Direction {
    val direction = when (this[0]) {
        'R' -> Direction.RIGHT
        'L' -> Direction.LEFT
        'U' -> Direction.UP
        'D' -> Direction.DOWN
        else -> throw IllegalArgumentException("Unknown direction: $this")
    }
    val steps = this.substring(1).toInt()
    return direction.move(steps)
}

