class Solution {
    fun uniquePathsWithObstacles(obstacleGrid: Array<IntArray>): Int {
        val m = obstacleGrid.size
        val n = obstacleGrid[0].size
        val dp = Array(m) { IntArray(n) }
        dp[0][0] = if (obstacleGrid[0][0] == 0) 1 else 0
        for (i in 1 until m) {
            dp[i][0] = if (obstacleGrid[i][0] == 0 && dp[i - 1][0] != 0) 1 else 0
        }
        for (j in 1 until n) {
            dp[0][j] = if (obstacleGrid[0][j] == 0 && dp[0][j - 1] != 0) 1 else 0
        }
        for (i in 1 until m) {
            for (j in 1 until n) {
                dp[i][j] = if (obstacleGrid[i][j] == 0 && dp[i - 1][j] != 0 && dp[i][j - 1] != 0) 1 else 0
            }
        }
        return dp[m - 1][n - 1]
    }
}

fun maxSumAfterPartitioning(arr: IntArray, k: Int): Int {
    val n = arr.size
    val dp = IntArray(n + 1)
    var max = 0
    for (i in 1..n) {
        var min = 1
        var maxSum = 0
        for (j in 1..k) {
            val curMax = min.coerceAtLeast(maxSum + arr[i - j])
            val curMin = j.coerceAtLeast(maxSum + arr[i - j])
            maxSum = curMax
            min = curMin
        }
        dp[i] = maxSum
        max = max.coerceAtLeast(dp[i])
    }
    return dp[n]
}

