class SegTree(val arr: Array<Int>) {
    var tree = Array(4 * arr.size) { 0 }

    fun build(index: Int, left: Int, right: Int) {
        if (left == right) {
            tree[index] = arr[left]
            return
        }
        val mid = left + (right - left) / 2
        build(2 * index + 1, left, mid)
        build(2 * index + 2, mid + 1, right)
        tree[index] = tree[2 * index + 1] + tree[2 * index + 2]
    }

    fun update(index: Int, left: Int, right: Int, i: Int, value: Int) {
        if (left == right) {
            tree[index] = value
            return
        }
        val mid = left + (right - left) / 2
        if (i <= mid) {
            update(2 * index + 1, left, mid, i, value)
        } else {
            update(2 * index + 2, mid + 1, right, i, value)
        }
        tree[index] = tree[2 * index + 1] + tree[2 * index + 2]
    }

    fun getSum(index: Int, left: Int, right: Int, start: Int, end: Int): Int {
        if (start > end || end < left || right < start) {
            return 0
        }
        if (start <= left && right <= end) {
            return tree[index]
        }
        val mid = left + (right - left) / 2
        return getSum(2 * index + 1, left, mid, start, end) + getSum(2 * index + 2, mid + 1, right, start, end)
    }
}

