    var i = low
    var j = high + 1
    while (true) {
        while (arr[++i] < pivot) if (i == high) break
        while (pivot < arr[--j]) if (j == low) break
        if (j <= i) break
        swap(arr, i, j)
    }
    swap(arr, i, low)
    return i
}

