fun threeSumClosest(nums: IntArray, target: Int): Int {
    nums.sort()
    var closest = nums[0] + nums[1] + nums[2]
    for (i in 0..nums.size - 3) {
        var j = i + 1
        var k = nums.size - 1
        while (j < k) {
            val sum = nums[i] + nums[j] + nums[k]
            if (sum == target) {
                return sum
            }
            if (Math.abs(sum - target) < Math.abs(closest - target)) {
                closest = sum
            }
            if (sum < target) {
                j++
            } else {
                k--
            }
        }
    }
    return closest
}

