class SolutionLCP13 {
    fun minimalSteps(board: Array<String>): Int {
        val n = board.size
        val m = board[0].length
        val dp = Array(n) { IntArray(m) }
        for (i in 0 until n) {
            for (j in 0 until m) {
                if (board[i][j] == 'S') {
                    dp[i][j] = 0
                } else if (board[i][j] == 'O') {
                    dp[i][j] = Int.MAX_VALUE
                } else {
                    dp[i][j] = Int.MAX_VALUE - 1
                }
            }
        }

        for (i in 0 until n) {
            for (j in 0 until m) {
                if (board[i][j] == 'S') {
                    if (i > 0 && dp[i - 1][j] != Int.MAX_VALUE) {
                        dp[i][j] = minOf(dp[i][j], dp[i - 1][j] + 1)
                    }
                    if (j > 0 && dp[i][j - 1] != Int.MAX_VALUE) {
                        dp[i][j] = minOf(dp[i][j], dp[i][j - 1] + 1)
                    }
                }
            }
        }

        for (i in n - 1 downTo 0) {
            for (j in m - 1 downTo 0) {
                if (board[i][j] == 'T') {
                    if (i < n - 1 && dp[i + 1][j] != Int.MAX_VALUE) {
                        dp[i][j] = minOf(dp[i][j], dp[i + 1][j] + 1)
                    }
                    if (j < m - 1 && dp[i][j + 1] != Int.MAX_VALUE) {
                        dp[i][j] = minOf(dp[i][j], dp[i][j + 1] + 1)
                    }
                }
            }
        }

        for (i in 0 until n) {
            for (j in 0 until m) {
                if (board[i][j] == 'O') {
                    dp[i][j] = Int.MAX_VALUE
                }
            }
        }

        return dp[0][0]
    }
}

