class UniquePaths {
    fun dynamicProgramming(m: Int, n: Int): Int {
        val dp = Array(m) { IntArray(n) }
        for (i in 0 until m) {
            for (j in 0 until n) {
                if (i == 0 && j == 0) {
                    dp[i][j] = 1
                } else if (i == 0) {
                    dp[i][j] = dp[i][j - 1]
                } else if (j == 0) {
                    dp[i][j] = dp[i - 1][j]
                } else {
                    dp[i][j] = dp[i - 1][j] + dp[i][j - 1]
                }
            }
        }
        return dp[m - 1][n - 1]
    }

    fun recursive(m: Int, n: Int): Int {
        if (m == 1 && n == 1) {
            return 1
        }
        if (m == 1) {
            return recursive(m, n - 1)
        }
        if (n == 1) {
            return recursive(m - 1, n)
        }
        return recursive(m - 1, n) + recursive(m, n - 1)
    }
}

