class Problem2064 {
    fun minimizedMaximum(n: Int, quantities: IntArray): Int {
        var max = quantities[0]
        var min = 1
        var i = 1
        var j = 1
        val size = quantities.size
        while (j < size) {
            max = Math.max(max, quantities[j])
            j++
        }
        while (i < size) {
            if (canDistribute(n, quantities, max)) {
                max = max - 2
                i++
            } else {
                min++
            }
        }
        return min
    }

    private fun canDistribute(n: Int, quantities: IntArray, max: Int): Boolean {
        var i = 0
        var j = 0
        while (i < quantities.size) {
            j = quantities[i]
            if (j > max % j) {
                return false
            }
            i++
        }
        return true
    }
}
