class LongestPalindromeSolution {
    fun longestPalindrome(s: String): String {
        val len = s.length
        val dp = Array(len) { IntArray(len) }
        var maxLen = 0
        var maxStart = 0
        for (i in 0 until len) {
            for (j in i + 1 until len) {
                if (s[i] == s[j]) {
                    if (j - i < 3) {
                        dp[i][j] = 2
                    } else {
                        dp[i][j] = dp[i + 1][j - 1] + 2
                    }
                    if (dp[i][j] > maxLen) {
                        maxLen = dp[i][j]
                        maxStart = i
                    }
                }
            }
        }
        return s.substring(maxStart, maxStart + maxLen)
    }
}

