class MaxArea {
    fun maxArea(height: IntArray): Int {
        var maxArea = 0
        var left = 0
        var right = 0
        var leftHeight = 0
        var rightHeight = 0
        for (i in 1 until height.size) {
            leftHeight = height[i - 1]
            rightHeight = height[i]
            if (leftHeight < rightHeight) {
                left = i - 1
                right = i
                maxArea = max(maxArea, (right - left) * rightHeight)
            } else if (leftHeight > rightHeight) {
