class BinaryGap {
    fun solution(N: Int): Int {
        var maxGap = 0
        var currentGap = 0
        var lastNonZeroDigitIndex = -1

        for (i in N.toString().indices) {
            val digit = N.toString()[i]
            if (digit == '1') {
                if (currentGap > maxGap) {
                    maxGap = currentGap
                }
                currentGap = 0
                lastNonZeroDigitIndex = i
            } else {
                currentGap++
            }
        }

        return if (lastNonZeroDigitIndex >= 0 && currentGap > maxGap) {
            currentGap
        } else {
            0
        }
    }
}

