class Solution {
    fun uniquePathsWithObstacles(obstacleGrid: Array<IntArray>): Int {
        val m = obstacleGrid.size
        val n = obstacleGrid[0].size
        val dp = Array(m) { IntArray(n) }
        dp[0][0] = if (obstacleGrid[0][0] == 0) 1 else 0
        for (i in 1..m - 1) {
            dp[i][0] = if (obstacleGrid[i][0] == 0 && dp[i - 1][0] != 0) 1 else 0
        }
        for (j in 1..n - 1) {
            dp[0][j] = if (obstacleGrid[0][j] == 0 && dp[0][j - 1] != 0) 1 else 0
        }
        for (i in 1..m - 1) {
            for (j in 1..n - 1) {
                dp[i][j] = if (obstacleGrid[i][j] == 0 && dp[i - 1][j] != 0 && dp[i][j - 1] != 0) dp[i - 1][j] + dp[i][j - 1] else 0
            }
        }
        return dp[m - 1][n - 1]
    }
}

