public final class QuickSortKt {
    public static final void quickSort(int[] array) {
        Intrinsics.checkNotNullParameter(array, "array")
        quickSort$inner(array, 0, array.size - 1)
    }

    private static final int quickSort$partition(int[] array, int low, int high) {
        int pivot = array[low];
        int i = low + 1;
        int j = high;
        while (true) {
            while (i <= high && array[i] <= pivot) {
                i++;
            }
            while (j >= low && array[j] >= pivot) {
                j--;
            }
            if (i > j) {
                break;
            }
            int tmp = array[i];
            array[i] = array[j];
            array[j] = tmp;
            i++;
            j--;
        }
        array[low] = array[j];
        array[j] = pivot;
        return j;
    }

    private static final void quickSort$inner(int[] array, int low, int high) {
        if (low < high) {
            int pivot = quickSort$partition(array, low, high);
            quickSort$inner(array, low, pivot - 1);
            quickSort$inner(array, pivot + 1, high);
        }
    }
}

