class UniquePaths {
    fun dynamicProgramming(m: Int, n: Int): Int {
        var dp = IntArray(n)
        for (i in 0..m - 1) {
            for (j in i..m - 1) {
                dp[j] = dp[j] + dp[j - 1]
            }
        }
        return dp[m - 1]
    }

    fun recursive(m: Int, n: Int): Int {
        if (m == 1 || n == 1) return 1
        if (m == n) return 1
        return recursive(m - 1, n) + recursive(m, n - 1)
    }
}

