class LongestPalindromeSolution {
    fun longestPalindrome(s: String): String {
        val len = s.length
        if (len < 2) return s
        val dp = Array(len) { BooleanArray(len) }
        var maxLen = 1
        var maxStart = 0
        for (i in 1 until len) {
            for (j in 0 until i) {
                if (s[i] == s[j] && (i - j < 3 || dp[j + 1][i - 1])) {
                    dp[j][i] = true
                    if (i - j + 1 > maxLen) {
                        maxLen = i - j + 1
                        maxStart = j
                    }
                }
            }
        }
        return s.substring(maxStart, maxStart + maxLen)
    }
}

