    fun findMedianSortedArrays(nums1: IntArray, nums2: IntArray): Double {
        val m = nums1.size
        val n = nums2.size
        val totalLength = m + n
        val totalLengthIsOdd = totalLength % 2 == 1
        val medianIndex = totalLength / 2
        var leftIndex = 0
        var rightIndex = 0
        var leftArrayIndex = 0
        var rightArrayIndex = 0
        var leftArrayLength = 0
        var rightArrayLength = 0
        var leftArrayValue = 0
        var rightArrayValue = 0
        var median = 0.0
        while (leftIndex <= medianIndex && rightIndex <= medianIndex) {
            leftArrayLength = m - leftArrayIndex
            rightArrayLength = n - rightArrayIndex
            if (leftArrayLength == 0) {
                median = rightArrayValue
                break
            }
            if (rightArrayLength == 0) {
                median = leftArrayValue
                break
            }
            leftArrayValue = if (leftArrayIndex < m) nums1[leftArrayIndex] else Int.MAX_VALUE
            rightArrayValue = if (rightArrayIndex < n) nums2[rightArrayIndex] else Int.MAX_VALUE
            if (leftArrayValue < rightArrayValue) {
                leftArrayIndex++
                leftIndex++
            } else {
                rightArrayIndex++
                rightIndex++
            }
        }
        if (totalLengthIsOdd) {
            return median.toDouble()
        }
        leftArrayLength = m - leftArrayIndex
        rightArrayLength = n - rightArrayIndex
        if (leftArrayLength == 0) {
            return rightArrayValue.toDouble()
        }
        if (rightArrayLength == 0) {
            return leftArrayValue.toDouble()
        }
        median = (leftArrayValue + rightArrayValue) / 2.0
        return median
    }
}

