class SegmentTree(val a: Array<Int>) {
    val n = a.size
    val segtree = Array(4 * n) { 0 }

    init {
        build(a, 0, 0, n - 1)
    }

    fun build(a: Array<Int>, node: Int, left: Int, right: Int) {
        if (left == right) {
            segtree[node] = a[left]
            return
        }

        val mid = (left + right) / 2
        build(a, 2 * node + 1, left, mid)
        build(a, 2 * node + 2, mid + 1, right)
        segtree[node] = segtree[2 * node + 1] + segtree[2 * node + 2]
    }

    fun getSum(left: Int, right: Int): Int {
        return getSum(0, left, right)
    }

    fun getSum(node: Int, left: Int, right: Int): Int {
        if (left > right) {
            return 0
        }

        if (segtree[node] != 0 && left in segtree[node * 2 + 1]..segtree[node * 2 + 2] && right in segtree[node * 2 + 1]..segtree[node * 2 + 2]) {
            return segtree[node]
        }

        val mid = (left + right) / 2
        return getSum(node * 2 + 1, left, mid) + getSum(node * 2 + 2, mid + 1, right)
    }
}

