class SegmentTree<T>(private val root: SegmentTreeNode<T>) {
    fun queryOrNull(x: Double): T? {
        if (!root.contains(x)) return null
        return root.query(x)
    }

    fun query(x: Double): T {
        val result = root.query(x)
        if (result == null) throw IllegalStateException("$x not found")
        return result
    }
}

class UniquePaths {
    fun dynamicProgramming(m: Int, n: Int): Int {
        val dp = Array(m) { IntArray(n) }
        for (i in 0 until m) {
            for (j in 0 until n) {
                if (i == 0 || j == 0) {
                    dp[i][j] = 1
                } else {
                    dp[i][j] = dp[i - 1][j] + dp[i][j - 1]
                }
            }
        }
        return dp[m - 1][n - 1]
    }

    fun recursive(m: Int, n: Int): Int {
        if (m == 1 || n == 1) {
            return 1
        }
        return recursive(m - 1, n) + recursive(m, n - 1)
    }
}

