    fun findMedianSortedArrays(nums1: IntArray, nums2: IntArray): Double {
        val merged = merge(nums1, nums2)
        val mid = merged.size / 2
        return if (merged.size % 2 == 0) {
            (merged[mid - 1] + merged[mid]) / 2.0
        } else {
            merged[mid]
        }
    }

