        val result = IntArray(nums1.size + nums2.size)
        var i = 0
        var j = 0
        var k = 0

        while (i < nums1.size && j < nums2.size) {
            if (nums1[i] < nums2[j]) {
                result[k++] = nums1[i++]
            } else {
                result[k++] = nums2[j++]
            }
        }

        while (i < nums1.size) {
            result[k++] = nums1[i++]
        }

        while (j < nums2.size) {
            result[k++] = nums2[j++]
        }

        return result
    }
}

