class LinkedList<T : Comparable<T>> {
    private var head: Node<T>? = null

    fun add(element: T) {
        val newNode = Node(element)
        if (head == null) {
            head = newNode
        } else {
            var current = head
            while (current?.next != null) {
                current = current.next
            }
            current?.next = newNode
        }
    }

    fun reverseRecurrsive(): LinkedList<T> {
        var current = head
        head = null
        reverseRecurrsiveHelper(current)
        return this
    }

    private fun reverseRecurrsiveHelper(node: Node<T>?) {
        if (node == null) {
            return
        }
        reverseRecurrsiveHelper(node.next)
        node.next = head
        head = node
    }

    override fun toString(): String {
        val elements = mutableListOf<T>()
        var current = head
        while (current != null) {
            elements.add(current.value)
            current = current.next
        }
        return elements.joinToString(", ")
    }

    private inner class Node<T>(val value: T, var next: Node<T>? = null)
}

