        val dateParts = date.split(".")
        val year = dateParts[0].toInt()
        val month = dateParts[1].toInt()
        val day = dateParts[2].toInt()

        val validityPeriodsArray = validityPeriods.map { it.split(".") }
        val validDays = mutableListOf<Int>()

        for (period in validityPeriodsArray) {
            val startYear = period[0].toInt()
            val startMonth = period[1].toInt()
            val startDate = period[2].toInt()
            val endYear = period[3].toInt()
            val endMonth = period[4].toInt()
            val endDate = period[5].toInt()

            if (year >= startYear && year <= endYear) {
                if (year == startYear && month < startMonth) {
                    continue
                }
                if (year == endYear && month > endMonth) {
                    continue
                }
                if (year == startYear && month == startMonth && day < startDate) {
                    continue
                }
                if (year == endYear && month == endMonth && day > endDate) {
                    continue
                }
                validDays.add(day)
            }
        }

        return validDays.size
    }
}

