class P12952 {
    fun solution(n: Int): Int {
        val visited = Array(n) { BooleanArray(n) }
        return dfs(visited, n)
    }

    private fun dfs(visited: Array<BooleanArray>, n: Int, row: Int = 0, col: Int = 0): Int {
        if (row == n) return 1
        if (col == n) return dfs(visited, n, row + 1, 0)

        var count = 0
        for (i in 0 until n) {
            if (!visited[row][i]) {
                visited[row][i] = true
                if (valid(visited, n, row, i)) {
                    count += dfs(visited, n, row, i)
                }
                visited[row][i] = false
            }
        }
        return count
    }

    private fun valid(visited: Array<BooleanArray>, n: Int, row: Int, col: Int): Boolean {
        for (i in 0 until n) {
            if (visited[row][i]) return false
            if (visited[i][col]) return false
        }
        for (i in 0 until n) {
            for (j in 0 until n) {
                if (visited[i][j] && abs(row - i) == abs(col - j)) return false
            }
        }
        return true
    }

