class SegmentTree(private val nums: IntArray) {
    fun getSum(left: Int, right: Int): Int {
        return getSumHelper(nums, left, right, 0, nums.size - 1)
    }

    private fun getSumHelper(nums: IntArray, left: Int, right: Int, start: Int, end: Int): Int {
        if (left > end || right < start) return 0
        if (left <= start && right >= end) return nums[start]
        val mid = (start + end) / 2
        return getSumHelper(nums, left, right, start, mid) + getSumHelper(nums, left, right, mid + 1, end)
    }
}

