class CyclicRotation {

    fun solution(a: IntArray, k: Int): IntArray {
        val n = a.size
        if (n == 0 || k % n == 0) return a

        val deque = ArrayDeque<Int>()
        for (num in a) {
            deque.add(num)
        }

        while (k > 0) {
            val last = deque.removeLast()
            deque.addFirst(last)
            k--
        }

        return Array(n) { deque[it] }
    }
}

