fun partition(arr: IntArray, left: Int, right: Int): Int {
    checkNotNullParameter(arr, "arr")
    val pivot = arr[right]
    var i = left - 1

    for (j in left..right - 1) {
        if (arr[j] < pivot) {
            i++
            arr[i] = arr[j]
            arr[j] = arr[i]
        }
    }

    arr[i + 1] = arr[right]
    arr[right] = arr[i + 1]
    return i + 1
}

