        nums.sort()
        var closest = nums[0] + nums[1] + nums[2]
        for (i in 0 until nums.size - 2) {
            var left = i + 1
            var right = nums.size - 1
            while (left < right) {
                val sum = nums[i] + nums[left] + nums[right]
                if (Math.abs(sum - target) < Math.abs(closest - target)) {
                    closest = sum
                }
                if (sum == target) {
                    return target
                } else if (sum < target) {
                    left++
                } else {
                    right--
                }
            }
        }
        return closest
    }
}

