class Problem2064 {
    fun minimizedMaximum(n: Int, quantities: IntArray): Int {
        checkNotNull(quantities) { "quantities cannot be null" }
        var maxQuantity = 1
        for (quantity in quantities) {
            maxQuantity = maxOf(maxQuantity, quantity)
        }
        var left = 1
        var right = maxQuantity
        while (left < right) {
            val mid = (left + right + 1) / 2
            if (canDistribute(n, quantities, mid)) {
                left = mid
            } else {
                right = mid - 1
            }
        }
        return left
    }

    private fun canDistribute(n: Int, quantities: IntArray, maxQuantity: Int): Boolean {
        var remaining = n
        for (quantity in quantities) {
            remaining -= Math.min(quantity, maxQuantity)
            if (remaining < 0) {
                return false
            }
        }
        return true
    }
}
