/**
 * This function takes a list of arm values and a list of rewards for each arm.
 * It updates the arm values based on the rewards earned.
 *
 * @param armValues A list of arm values.
 * @param rewards A list of rewards earned for each arm.
 */
fun updateArmValues(armValues: MutableList<Int>, rewards: List<Float>) {
    for (i in armValues.indices) {
        if (rewards[i] > 0) {
            armValues[i]++
        } else {
            armValues[i]--
        }
    }
}

