 * and returns a list of planets that are not visible from any star.
 *
 * @param planets A list of planets.
 * @param stars A list of stars.
 * @return A list of planets that are not visible from any star.
 */
fun findVisiblePlanets(planets: List<String>, stars: List<String>): List<String> {
    val visiblePlanets = mutableListOf<String>()

    for (planet in planets) {
        var isVisible = true
        for (star in stars) {
            if (star in planet) {
                isVisible = false
                break
            }
        }
        if (isVisible) {
            visiblePlanets.add(planet)
        }
    }

    return visiblePlanets
}

