/**
 * This function takes a list of integers as input and returns the sum of all the parts of the number.
 *
 * @param numbers A list of integers.
 * @return The sum of all the parts of the number.
 */
fun sumOfParts(numbers: List<Int>): Int {
    var totalSum = 0
    for (number in numbers) {
        var tempNumber = number
        while (tempNumber != 0) {
            totalSum += tempNumber % 10
            tempNumber /= 10
        }
    }
    return totalSum
}

