/**
 * This function takes a list of planets and their orbits and returns the number of unique orbits.
 *
 * @param planets A list of planets.
 * @param orbits A list of pairs representing orbits. Each pair contains the planet name and the planet's parent planet.
 * @return The number of unique orbits.
 */
fun countUniqueOrbits(planets: List<String>, orbits: List<Pair<String, String>>): Int {
    val uniqueOrbits = mutableSetOf<String>()

    for (planet in planets) {
        var currentPlanet = planet
        while (true) {
            val orbit = orbits.find { it.first == currentPlanet }
            if (orbit != null) {
                uniqueOrbits.add(orbit.second)
                currentPlanet = orbit.second
            } else {
                break
            }
        }
    }

    return uniqueOrbits.size
}

