 *
 * @param celestialObjects A list of integers representing the coordinates of an astronomer's celestial objects.
 * @return A list of pairs where each pair contains the original coordinates and their distance from the origin.
 */
fun calculateDistance(celestialObjects: List<Int>): List<Pair<Int, Int>> {
    val distances = mutableListOf<Pair<Int, Int>>()
    for (objectCoords in celestialObjects) {
        val distance = Math.abs(objectCoords)
        distances.add(Pair(objectCoords, distance))
    }
    return distances
}

