 *
 * @param map The map to which the path needs to be added.
 * @param path The path to be added to the map.
 * @return The updated map with the path added.
 */
fun addPath(map: String, path: String): String {
    // Check if the map and path are not null
    requireNotNull(map) { "Map cannot be null" }
    requireNotNull(path) { "Path cannot be null" }

