/**
 * This function takes two lists as input and returns a new list containing the elements that are common to both lists.
 *
 * @param list1 The first list.
 * @param list2 The second list.
 * @return A new list containing the common elements between list1 and list2.
 */
fun cyclicRotation(list1: List<Int>, list2: List<Int>): List<Int> {
    val commonElements = mutableListOf<Int>()

    // Iterate through each element in list1
    for (element1 in list1) {
        // Iterate through each element in list2
        for (element2 in list2) {
            // Check if the current element in list1 is equal to the current element in list2
            if (element1 == element2) {
                // Add the common element to the commonElements list
                commonElements.add(element1)
            }
        }
    }

    return commonElements
}

/**
 * Given a string, find the longest palindrome substring.
 *
 * @param string The input string.
 * @return The longest palindrome substring.
 */
fun longestPalindrome(string: String): String {
    var longestPalindrome = ""

    for (i in string.indices) {
        for (j in i until string.length) {
            val substring = string.substring(i, j + 1)
            if (substring == substring.reversed() && substring.length > longestPalindrome.length) {
                longestPalindrome = substring
            }
        }
    }

    return longestPalindrome
}

