fun countOddOccurrences(numbers: List<Int>): Int {
    var count = 0
    val uniqueNumbers = numbers.toSet()

    for (number in uniqueNumbers) {
        if (number % 2 != 0) {
            count++
        }
    }

    return count
}

/**
