/**
 * Given a string, find the length of the longest substring without any repetitions.
 *
 * @param string The input string.
 * @return The length of the longest substring without any repetitions.
 */
fun lengthOfLongestSubstring(string: String): Int {
    var longestSubstringLength = 0

    for (i in string.indices) {
        var substringLength = 0

        for (j in i until string.length) {
            if (string[j] !in string.substring(i, j)) {
                substringLength++
            } else {
                break
            }
        }

        longestSubstringLength = maxOf(longestSubstringLength, substringLength)
    }

    return longestSubstringLength
}

