/**
 * This function takes two lists of integers as input and returns the median of the sorted list.
 *
 * @param list1 The first list of integers.
 * @param list2 The second list of integers.
 * @return The median of the sorted list.
 */
fun findMedianSortedArrays(list1: List<Int>, list2: List<Int>): Double {
    val combinedList = list1 + list2
    val sortedList = combinedList.sorted()
    val medianIndex = sortedList.size / 2

    return if (sortedList.size % 2 == 0) {
        (sortedList[medianIndex - 1] + sortedList[medianIndex]) / 2.0
    } else {
        sortedList[medianIndex].toDouble()
    }
}

fun hash(settlers: List<List<AreaElement>>): Int {
    // Check if the input is a valid list of lists
    if (settlers !is List<List<AreaElement>>) {
        return -1
    }

    // Initialize a set to store the unique elements
    val uniqueElements = mutableSetOf<AreaElement>()

    // Iterate over each list in the settlers list
    for (group in settlers) {
        // Iterate over each element in the group
        for (element in group) {
            // Check if the element is already in the uniqueElements set
            if (element !in uniqueElements) {
                // Add the element to the uniqueElements set
                uniqueElements.add(element)
            }
        }
    }

    // Return the length of the uniqueElements set
    return uniqueElements.size
}

/**
 * This function takes a list of integers and returns the missing element in the list.
 *
 * @param nums A list of integers.
 * @return The missing element in the list.
 */
fun findMissingElement(nums: List<Int>): Int {
    // Sort the list in ascending order
    val sortedNums = nums.sorted()

    // Iterate through the list from the first element to the last element
    for (i in 0 until sortedNums.size - 1) {
        // Check if the current element is not equal to the next element
        if (sortedNums[i] != sortedNums[i + 1]) {
            // Return the missing element
            return sortedNums[i] + 1
        }
    }

    // If no missing element is found, return the last element plus 1
    return sortedNums.last() + 1
}

/**
