fun oddOccurrencesInArray(arr: List<Int>): Int {
    // Check if the input is a list
    if (arr !is List<Int>) {
        throw IllegalArgumentException("Input must be a list.")
    }

    // Remove duplicates from the list
    val uniqueList = arr.toSet()

    // Initialize a counter for odd occurrences
    var oddCount = 0

    // Iterate through the unique list
    for (num in uniqueList) {
        // Check if the number occurs an odd number of times
        if (arr.count { it == num } % 2 != 0) {
            oddCount++
        }
    }

    return oddCount
}

