/**
 * This function takes a number as input and returns the Fibonacci sequence up to that number.
 */
fun fibonacciSequence(num: Int): List<Int> {
    val fibonacciSequence = mutableListOf<Int>()
    var previous = 0
    var current = 1

    while (current <= num) {
        fibonacciSequence.add(current)
        val temp = current
        current += previous
        previous = temp
    }

    return fibonacciSequence
}

/**
 * This function takes a positive integer n as input and returns the number of perfect squares
 * that can be formed using positive integers up to n.
 */
fun numSquares(n: Int): Int {
    var count = 0
    var num = 1

    while (num * num <= n) {
        count++
        num++
    }

    return count
}

/**
 * Given a list of numbers and a target number, find the indices of the numbers that sum up to the target.
 *
 * @param numbers A list of numbers.
 * @param target The target number.
 * @return A list of pairs representing the indices of the numbers that sum up to the target.
 */
fun twoSumInputSortArray(numbers: List<Int>, target: Int): List<Pair<Int, Int>> {
    val indices = mutableListOf<Pair<Int, Int>>()

    // Sort the numbers in ascending order
    val sortedNumbers = numbers.sorted()

    // Initialize the left and right indices
    var left = 0
    var right = sortedNumbers.size - 1

    while (left < right) {
        val sum = sortedNumbers[left] + sortedNumbers[right]

        if (sum == target) {
            indices.add(Pair(sortedNumbers[left], sortedNumbers[right]))
            left++
            right--
        } else if (sum < target) {
            left++
        } else {
            right--
        }
    }

    return indices
}

/**
 * This function takes two strings as input and finds the permutation of the second string within the first string.
 *
 * @param string1 The first string.
 * @param string2 The second string.
 * @return The permutation of the second string within the first string.
 */
fun findPermutation(string1: String, string2: String): String {
    var permutation = ""

    for (char1 in string1) {
        for (char2 in string2) {
            if (char1 == char2) {
                permutation += char1
            }
        }
    }

    return permutation
}

/**
 * Given a list of heights of buildings, find the maximum area possible by stacking buildings.
 *
 * @param heights A list of integers representing the heights of buildings.
 * @return The maximum area possible.
 */
fun maxArea(heights: List<Int>): Int {
    var maxArea = 0
    var stack = mutableListOf<Int>()

    for (height in heights) {
        while (stack.isNotEmpty() && stack.last() < height) {
            val area = stack.removeAt(stack.size - 1) * height
            maxArea = maxOf(maxArea, area)
        }
        stack.add(height)
    }

    while (stack.isNotEmpty()) {
        val area = stack.removeAt(stack.size - 1) * stack.last()
        maxArea = maxOf(maxArea, area)
    }

    return maxArea
}

/**
 * This function takes a list of prices and calculates the maximum profit that can be obtained by selling a stock.
 *
 * @param prices A list of prices representing the stock prices over a certain period.
 * @return The maximum profit that can be obtained by selling a stock.
 */
fun maxProfit(prices: List<Int>): Int {
    var maxProfit = 0
    var buyPrice = 0

    for (price in prices) {
        if (price < buyPrice) {
            buyPrice = price
        } else {
            maxProfit = maxOf(maxProfit, price - buyPrice)
        }
    }

    return maxProfit
}

/**
 * This function takes a list of strings and returns the number of overlapping lines.
 *
 * @param lines A list of strings representing the lines.
 * @return The number of overlapping lines.
 */
fun overlaps(lines: List<String>): Int {
    val overlappingLines = mutableListOf<String>()

    for (line1 in lines) {
        for (line2 in lines) {
            if (line1 != line2) {
                if (line1.contains(line2)) {
                    overlappingLines.add(line2)
                }
            }
        }
    }

    return overlappingLines.size
}

/**
 * Given two strings, check if the first string is a subsequence of the second string.
 *
 * @param s The first string.
 * @param t The second string.
 * @return True if s is a subsequence of t, False otherwise.
 */
fun isSubsequence(s: String, t: String): Boolean {
    var i = 0
    var j = 0

    while (i < s.length) {
        if (s[i] == t[j]) {
            i++
            j++
        } else {
            j++
        }

        if (j == t.length) {
            return true
        }
    }

    return false
}

/**
 * This function takes a positive integer n and calculates the Fibonacci sequence up to the nth term.
 * It uses tail recursion to optimize the calculation.
 */
fun fibonacciLargeTailRecursiveOptimisedGenerator(n: Int): BigInteger {
    var fib1 = BigInteger.ONE
    var fib2 = BigInteger.ZERO
    var fib3 = BigInteger.ZERO

    for (i in 0 until n) {
        fib3 = fib1 + fib2
        fib1 = fib2
        fib2 = fib3
    }

    return fib3
}

/**
 * This function takes a number of trees as input and returns the number of unique binary search trees
 * that can be created with that number of trees.
 */
fun numTrees(numTrees: Int): Int {
    var uniqueTrees = 0

    // Iterate through all possible combinations of trees
    for (i in 0 until numTrees) {
        for (j in 0 until numTrees) {
            for (k in 0 until numTrees) {
                // Check if the sum of the number of trees is equal to the number of trees
                if (i + j + k == numTrees) {
                    // Check if the number of unique trees is already greater than the maximum possible
                    if (uniqueTrees < (1 shl (numTrees - 1))) {
                        uniqueTrees++
                    }
                }
            }
        }
    }

    return uniqueTrees
}

