/**
 * This function takes a list of strings representing the validity periods of personal information collection
 * and returns the intersection of the periods that are valid for all the collections.
 *
 * @param validityPeriods A list of strings representing the validity periods of personal information collection.
 * @return A string representing the intersection of the valid periods.
 */
fun intersectionOfValidityPeriods(validityPeriods: List<String>): String {
    // Initialize the intersection with the first validity period
    var intersection = validityPeriods[0]

    // Iterate through the remaining validity periods
    for (period in validityPeriods.subList(1, validityPeriods.size)) {
        // Check if the current period is valid for all collections
        if (intersection.all { period in it }) {
            // Update the intersection with the current period
            intersection = period
        } else {
            // If the current period is not valid for all collections, break the loop
            break
        }
    }

    return intersection
}

