 * @param number The number to find the bit count for.
 * @return The number of bits required to represent the given number.
 */
fun findBitCount(number: Int): Int {
    var bitCount = 0
    var temp = number
    while (temp != 0) {
        temp = temp shr 1
        bitCount++
    }
    return bitCount
}

