/**
 * Given a list of integers, find the missing number.
 *
 * @param nums A list of integers.
 * @return The missing number.
 */
fun missingNumber1(nums: List<Int>): Int {
    // Check if the list is empty
    if (nums.isEmpty()) {
        return -1
    }

    // Sort the list
    val sortedNums = nums.sorted()

    // Check if the first number is missing
    if (sortedNums[0] != 0) {
        return 0
    }

    // Check if the last number is missing
    if (sortedNums.last() != sortedNums.size) {
        return sortedNums.size
    }

    // Check if any number is missing between the first and last number
    for (i in 1 until sortedNums.size - 1) {
        if (sortedNums[i] != sortedNums[i - 1] + 1) {
            return sortedNums[i] - 1
        }
    }

    return -1
}

