/**
 * This function takes a positive integer n as input and returns the number of coins required to form a total sum of n.
 *
 * @param n A positive integer representing the total sum of coins.
 * @return The number of coins required to form the total sum.
 */
fun arrangingCoins(n: Int): Int {
    var coins = 0
    var remainingSum = n

    while (remainingSum > 0) {
        if (remainingSum >= 25) {
            remainingSum -= 25
            coins++
        } else if (remainingSum >= 10) {
            remainingSum -= 10
            coins++
        } else if (remainingSum >= 5) {
            remainingSum -= 5
            coins++
        } else {
            remainingSum -= 1
            coins++
        }
    }

    return coins
}

/**
 * This function takes a map and a character and an integer, and adds the character to the map with the integer as the value.
 * If the character already exists in the map, it increments the value by the given integer.
 */
fun addTo(map: MutableMap<Char, IntCount>, character: Char, value: Int) {
    if (map.containsKey(character)) {
        map[character] = map[character]!!.copy(value = map[character]!!.value + value)
    } else {
        map[character] = IntCount(value)
    }
}

// Given a list of obstacles and a list of paths, find the valid paths that are not blocked by any obstacle.
fun uniquePathsWithObstacles(obstacles: List<List<Int>>, paths: List<List<Int>>): List<List<Int>> {
    val validPaths = mutableListOf<List<Int>>()

    for (path in paths) {
        var blocked = false

        for (obstacle in obstacles) {
            if (obstacle[0] == path[0] && obstacle[1] == path[1]) {
                blocked = true
                break
            }
        }

        if (!blocked) {
            validPaths.add(path)
        }
    }

    return validPaths
}

