/**
 * Given a list of integers and a partition size, find the maximum sum
 * after partitioning the list into sublists of size at most partitionSize.
 *
 * @param arr A list of integers.
 * @param partitionSize An integer representing the maximum size of each sublist.
 * @return The maximum sum after partitioning the list.
 *
 * Example:
 * partitionArrayForMaximumSum(listOf(1, 2, 3, 4, 5), 2)
 * Output: 9
 */
fun partitionArrayForMaximumSum(arr: List<Int>, partitionSize: Int): Int {
    // Check if the partition size is valid
    if (partitionSize <= 0) {
        throw IllegalArgumentException("Invalid partition size.")
    }

    // Initialize the maximum sum
    var maxSum = 0

    // Iterate over the list in chunks of size at most partitionSize
    for (i in 0 until arr.size step partitionSize) {
        // Calculate the sum of the current chunk
        val chunkSum = arr.subList(i, minOf(i + partitionSize, arr.size)).sum()

        // Update the maximum sum if the chunk sum is greater
        maxSum = maxOf(maxSum, chunkSum)
    }

    return maxSum
}

