 */
fun majorityElement(nums: List<Int>): Int {
    var count = 0
    var majority: Int? = null

    for (num in nums) {
        if (count == 0) {
            majority = num
        }
        count += if (num == majority!!) 1 else -1
    }

    return majority!!
}

