fun dividers(n: Long): List<Long> {
    val dividers = mutableListOf<Long>()
    var num = n
    while (num > 1) {
        for (i in 2..num) {
            if (num % i == 0) {
                dividers.add(i.toLong())
                num /= i
                break
            }
        }
    }
    return dividers
}

fun primeFactors(n: Long): Map<Long, List<Long>> {
    val factors = mutableMapOf<Long, MutableList<Long>>()
    var num = n
    while (num > 1) {
        for (i in 2..num) {
            if (num % i == 0) {
                if (factors.containsKey(i)) {
                    factors[i]?.add(i.toLong())
                } else {
                    factors[i] = mutableListOf(i.toLong())
                }
                num /= i
                break
            }
        }
    }
    return factors
}

