/**
 * This function takes a positive integer as input and returns the largest palindrome number that is less than or equal to the given number.
 */
fun largestPalindrome(): Int {
    var largestPalindrome = 0
    for (num1 in 1..999) {
        for (num2 in 1..99) {
            val palindrome = num1 * num2
            if (palindrome <= largestPalindrome) {
                break
            }
            if (palindrome % 10 == 0) {
                continue
            }
            if (palindrome % 10 == palindrome / 10) {
                largestPalindrome = palindrome
            }
        }
    }
    return largestPalindrome
}

