/**
 * This function takes a complex number and returns its exponential.
 *
 * @param complexNumber The complex number for which exponential needs to be calculated.
 * @return The exponential of the complex number.
 */
fun exponential(complexNumber: ComplexNumber): ComplexNumber {
    // Check if the complex number is valid
    requireNotNull(complexNumber) { "Complex number cannot be null." }

    // Calculate the exponential
    val exponential = complexNumber.real.pow(2.0) + complexNumber.imaginary.pow(2.0).sqrt()

    // Create a new complex number with the exponential value
    val newComplexNumber = ComplexNumber(exponential, 0.0)

    return newComplexNumber
}

/**
 * Given a list of integers, find the longest Collatz sequence.
 *
 * @param numbers A list of integers.
 * @return The longest Collatz sequence.
 */
fun findLongestCollatzSequence(numbers: List<Int>): List<Int> {
    var longestSequence = emptyList<Int>()
    var maxLength = 0

    for (num in numbers) {
        val sequence = mutableListOf<Int>()
        var currentNum = num

        while (currentNum != 1) {
            sequence.add(currentNum)

            if (currentNum % 2 == 0) {
                currentNum /= 2
            } else {
                currentNum = currentNum * 3 + 1
            }
        }

        if (sequence.size > maxLength) {
            longestSequence = sequence
            maxLength = sequence.size
        }
    }

    return longestSequence
}

