 */
fun findGCD(num1: Int, num2: Int): Int {
    var gcd = 1

    for (i in 1..minOf(num1, num2)) {
        if (num1 % i == 0 && num2 % i == 0) {
            gcd = i
        }
    }

    return gcd
}

